let createComments=function(){let e=$("#new-comment-form");e.submit((function(t){t.preventDefault(),$.ajax({type:"post",url:"/comments/create",data:e.serialize(),success:function(e){let t=newCommentDom(e.data.comment);$("#post-comments-"+e.data.comment.post).append(t),deleteComment($(" .delete-comment-button",t)),new ToggleLike($(".toggle-like-button",t)),new Noty({theme:"relax",text:"Post published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))},newCommentDom=function(e){return $(`  \n    <li id="comment-${e._id}">\n        <p>\n                <samll>\n                    <a class="delete-comment-button" href="/comments/destroy/${e._id}">X</a>\n                </samll>\n            \n            ${e.content} \n            \n            <br>\n            <small>\n                ${e.user.name} \n            </small>\n            <small>\n                <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                    0 Likes\n                </a>\n            </small>\n        </p>\n    </li>\n   `)},deleteComment=function(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"comment deleted!",type:"success",layout:"topRight",timeout:1500}).show(),console.log("jiiii")},error:function(e){console.log(e.responseText)}})}))};createComments();